// Calculate conversion rates and performance metrics
const data = $input.first().json;

const metrics = {
  // Traffic Metrics
  unique_visitors: data.unique_visitors || 0,

  // Conversion Funnel
  quiz_completion_rate: data.unique_visitors > 0 ? 
    (data.quiz_completions / data.unique_visitors * 100).toFixed(2) : 0,

  payment_intent_rate: data.quiz_completions > 0 ?
    (data.payment_attempts / data.quiz_completions * 100).toFixed(2) : 0,

  sales_conversion_rate: data.payment_attempts > 0 ?
    (data.sales / data.payment_attempts * 100).toFixed(2) : 0,

  overall_conversion_rate: data.unique_visitors > 0 ?
    (data.sales / data.unique_visitors * 100).toFixed(2) : 0,

  // Revenue Metrics  
  total_sales: data.sales || 0,
  total_revenue: data.revenue || 0,
  average_order_value: data.sales > 0 ? 
    (data.revenue / data.sales).toFixed(2) : 0,
  revenue_per_visitor: data.unique_visitors > 0 ?
    (data.revenue / data.unique_visitors).toFixed(2) : 0,

  // Engagement Metrics
  avg_vsl_watch_time: data.avg_vsl_time || 0,

  // Performance Status
  performance_status: 'normal',
  alerts: []
};

// Check for alerts
if (metrics.unique_visitors < 10) {
  metrics.alerts.push('âš ï¸ Low traffic alert');
  metrics.performance_status = 'warning';
}

if (metrics.quiz_completion_rate < 20) {
  metrics.alerts.push('âš ï¸ Low quiz completion rate');
  metrics.performance_status = 'warning';
}

if (metrics.sales_conversion_rate < 1) {
  metrics.alerts.push('ðŸš¨ Low sales conversion');
  metrics.performance_status = 'critical';
}

if (metrics.total_revenue < 100) {
  metrics.alerts.push('ðŸ’° Revenue below target');
}

// Calculate hourly run rate
metrics.hourly_revenue_rate = metrics.total_revenue;
metrics.daily_revenue_projection = metrics.hourly_revenue_rate * 24;
metrics.monthly_revenue_projection = metrics.daily_revenue_projection * 30;

return [{ json: metrics }];

