// Automated optimization triggers based on performance
const metrics = $input.first().json;
const optimizations = [];

// Traffic Optimization
if (metrics.unique_visitors < 50) {
  optimizations.push({
    type: 'traffic_boost',
    action: 'increase_content_frequency',
    params: {
      tiktok_posts: 'double',
      blog_posts: 'add_trending_topics'
    }
  });
}

// Conversion Optimization
if (metrics.quiz_completion_rate < 30) {
  optimizations.push({
    type: 'quiz_optimization',
    action: 'ab_test_questions',
    params: {
      test_shorter_quiz: true,
      add_progress_bar: true
    }
  });
}

// Sales Optimization
if (metrics.sales_conversion_rate < 2) {
  optimizations.push({
    type: 'sales_optimization',
    action: 'adjust_pricing',
    params: {
      add_payment_plans: true,
      increase_urgency: true,
      add_guarantee: true
    }
  });
}

// Revenue Optimization
if (metrics.average_order_value < 400) {
  optimizations.push({
    type: 'aov_optimization',
    action: 'push_premium_products',
    params: {
      highlight_premium: true,
      add_bundles: true,
      upsell_intensity: 'high'
    }
  });
}

return optimizations.map(opt => ({ json: opt }));
